import React, { useState } from 'react';
import { Container, Form, Button, Alert } from 'react-bootstrap';

function AddClaimPage() {
  const [claim, setClaim] = useState({
    patient_id: '',
    status: '',
    amount: '',
  });

  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setClaim((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate input
    if (!claim.patient_id || !claim.status || !claim.amount) {
      setError('All fields are required');
      return;
    }

    try {
      const response = await fetch('http://127.0.0.1:5000/claims/add_claim', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(claim),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccessMessage('Claim added successfully!');
        setClaim({
          patient_id: '',
          status: '',
          amount: '',
        });
        setError(null);
      } else {
        setError(data.error || 'Error adding claim');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Something went wrong');
    }
  };

  return (
    <Container className="mt-4">
      <h2>Add New Claim</h2>

      {error && <Alert variant="danger">{error}</Alert>}
      {successMessage && <Alert variant="success">{successMessage}</Alert>}

      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="formPatientId">
          <Form.Label>Patient ID</Form.Label>
          <Form.Control
            type="text"
            name="patient_id"
            placeholder="Enter Patient ID"
            value={claim.patient_id}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formStatus">
          <Form.Label>Status</Form.Label>
          <Form.Control
            type="text"
            name="status"
            placeholder="Enter Status"
            value={claim.status}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formAmount">
          <Form.Label>Amount</Form.Label>
          <Form.Control
            type="number"
            name="amount"
            placeholder="Enter Amount"
            value={claim.amount}
            onChange={handleChange}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Add Claim
        </Button>
      </Form>
    </Container>
  );
}

export default AddClaimPage;
