import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Spinner, Alert, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';  // For navigation (React Router v6)

function PatientClaimsPage() {
  const [claim, setClaims] = useState({
    patient_id: '',
    status: '',
    amount: '',
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate(); // For navigating to another page

  useEffect(() => {
    fetch('http://127.0.0.1:5000/claims/get_claims')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch patients');
        }
        return response.json();
      })
      .then((data) => {
        setClaims(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error:', error);
        setError(error.message);
        setLoading(false);
      });
  }, []);
  const handleSubmit = async (e) => {
    e.preventDefault();
  try {
    const response = await fetch('http://127.0.0.1:5000/patients/patient', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(claim)
    });

    const data = await response.json();

    if (response.ok) {
      alert('Patient added successfully!');
      setPatient({ id: '', name: '', insurance_id: '' });
    } else {
      alert('Error: ' + data.error);
    }

  } catch (error) {
    console.error('Error:', error);
    alert('Something went wrong');
  }
};

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Patient Claims</h2>

      {loading && (
        <div className="text-center">
          <Spinner animation="border" />
        </div>
      )}

      {error && <Alert variant="danger">{error}</Alert>}

      <Row>
        {patients.map((patient) => (
          <Col md={6} lg={4} key={patient.id} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{patient.name}</Card.Title>
                <Card.Text>
                  <strong>ID:</strong> {patient.id} <br />
                  <strong>Insurance ID:</strong> {patient.insurance_id}
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Button to navigate to Add Patient Page */}
      <div className="text-center mt-4">
        <Button variant="primary" onClick={handleButtonClick}>
          Add New Patient
        </Button>
      </div>
    </Container>
  );
}

export default PatientClaimsPage;
