import React, { useState } from 'react';
import { Button, Form, Container } from 'react-bootstrap';

function AddClaimPage() {
  const [claim, setClaim] = useState({
    patient_id: '',
    status: '',
    amount: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setClaim((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const patientId = claim.patient_id; // Get patient_id from the form

    try {
      const response = await fetch(`http://127.0.0.1:5000/claims/submit/${patientId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(claim), // sending the claim details
      });

      const data = await response.json();

      if (response.ok) {
        alert('Claim added successfully!');
        setClaim({ patient_id: '', status: '', amount: '' });
      } else {
        alert('Error: ' + data.error);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Something went wrong');
    }
  };

  return (
    <Container>
      <h2 className="mt-5">Add Claim</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="formPatientId">
          <Form.Label>Patient ID</Form.Label>
          <Form.Control
            type="number"
            name="patient_id"
            placeholder="Enter Patient ID"
            value={claim.patient_id}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formStatus">
          <Form.Label>Status</Form.Label>
          <Form.Control
            type="text"
            name="status"
            placeholder="Enter claim status"
            value={claim.status}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formAmount">
          <Form.Label>Claim Amount</Form.Label>
          <Form.Control
            type="number"
            name="amount"
            placeholder="Enter claim amount"
            value={claim.amount}
            onChange={handleChange}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Add Claim
        </Button>
      </Form>
    </Container>
  );
}

export default AddClaimPage;
